import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Form.css';

const AddPackagingMaterialForm = () => {
  const [formData, setFormData] = useState({
    packagingMaterial: '',
    internalMeasurement: '',
    weightLimit: '',
    description: '',
    uploadImage: null,
  });
  const [errors, setErrors] = useState({});
  const [materials, setMaterials] = useState([]); // To hold materials list
  const [editingId, setEditingId] = useState(null); // To track editing

  // Fetch existing materials
  useEffect(() => {
    axios.get('http://localhost:5000/api/packaging-materials').then((response) => {
      setMaterials(response.data);
    });
  }, []);

  // Handle input changes
  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [id]: value }));
  };

  // Handle file input changes
  const handleFileChange = (e) => {
    setFormData((prevData) => ({ ...prevData, uploadImage: e.target.files[0] }));
  };

  // Validate form
  const validateForm = () => {
    const newErrors = {};

    // Required Fields Validation
    if (!formData.packagingMaterial) {
      newErrors.packagingMaterial = 'Packaging material is required.';
    }

    if (!formData.internalMeasurement) {
      newErrors.internalMeasurement = 'Internal measurement is required.';
    }

    if (!formData.weightLimit) {
      newErrors.weightLimit = 'Weight limit is required.';
    } else if (isNaN(formData.weightLimit)) {
      newErrors.weightLimit = 'Weight limit should be a number.';
    }

    if (!formData.description) {
      newErrors.description = 'Description is required.';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      try {
        if (editingId) {
          // Update material
          await axios.put(`http://localhost:5000/api/packaging-materials/${editingId}`, formData);
          setEditingId(null);
        } else {
          // Add new material
          await axios.post('http://localhost:5000/api/packaging-materials', formData);
        }
        setFormData({
          packagingMaterial: '',
          internalMeasurement: '',
          weightLimit: '',
          description: '',
          uploadImage: null,
        });
        alert('Material saved successfully!');
      } catch (error) {
        console.error('Error saving material:', error);
      }
    }
  };

  // Handle delete material
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/packaging-materials/${id}`);
      alert('Material deleted successfully!');
    } catch (error) {
      console.error('Error deleting material:', error);
    }
  };

  // Handle edit material
  const handleEdit = (material) => {
    setFormData({
      packagingMaterial: material.packagingMaterial,
      internalMeasurement: material.internalMeasurement,
      weightLimit: material.weightLimit,
      description: material.description,
      uploadImage: material.uploadImage,
    });
    setEditingId(material._id);
  };

  return (
    <div className="form-container">
      <div className="form-header">
        <h2>{editingId ? 'Edit' : 'Add'} Packaging Materials</h2>
      </div>
      <form className="packaging-form" onSubmit={handleSubmit}>
        {/* Form fields */}
        {/* Add the same form structure here as before */}
        <div className="form-group">
          <button type="submit" className="submit-btn">{editingId ? 'Update' : 'Add'}</button>
        </div>
      </form>

      {/* List of added materials */}
      <div className="materials-list">
        <h3>Packaging Materials List</h3>
        <ul>
          {materials.map((material) => (
            <li key={material._id}>
              {material.packagingMaterial} - {material.internalMeasurement}
              <button onClick={() => handleEdit(material)}>Edit</button>
              <button onClick={() => handleDelete(material._id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default AddPackagingMaterialForm;
