import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './materials.css';
import AddMaterials from './AddMaterials.jsx';

const Materials = () => {
  const [materials, setMaterials] = useState([]);
  const [editIndex, setEditIndex] = useState(-1);
  const [editFormData, setEditFormData] = useState({});

  // Fetch materials from MongoDB
  const fetchMaterials = async () => {
    try {
      const response = await axios.get('/api/packaging-materials');
      setMaterials(response.data);
    } catch (error) {
      console.error('Error fetching materials:', error);
    }
  };

  useEffect(() => {
    fetchMaterials();
  }, []);

  // Add a handler for adding materials
  const handleMaterialAdded = () => {
    fetchMaterials(); // Refresh the materials list after adding
  };

  // Handle input changes during edit
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditFormData({ ...editFormData, [name]: value });
  };

  // Enable edit mode for a specific row
  const handleEditClick = (index) => {
    setEditIndex(index);
    setEditFormData(materials[index]);
  };

  // Save the edited row
  const handleSaveClick = async () => {
    try {
      const response = await axios.put(`/api/packaging-materials/${editFormData._id}`, editFormData);
      const updatedMaterials = materials.map((material, index) => 
        index === editIndex ? response.data : material
      );
      setMaterials(updatedMaterials);
      setEditIndex(-1); // Exit edit mode
    } catch (error) {
      console.error('Error saving material:', error);
    }
  };

  // Delete a row
  const handleDeleteClick = async (id) => {
    try {
      await axios.delete(`/api/packaging-materials/${id}`);
      setMaterials(materials.filter((material) => material._id !== id));
    } catch (error) {
      console.error('Error deleting material:', error);
    }
  };

  return (
    <div className="table-container">
      <h2>Packaging Materials</h2>
      <AddMaterials onMaterialAdded={handleMaterialAdded} />
      <table className="packaging-table">
        <thead>
          <tr>
            <th>Id</th>
            <th>Material</th>
            <th>Internal Measurements</th>
            <th>Weight Limit</th>
            <th>Description</th>
            <th>Last Modified</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {materials.map((material, index) => (
            <tr key={material._id}>
              {editIndex === index ? (
                <>
                  <td>{material._id}</td> {/* Use material._id for correct key */}
                  <td>
                    <input
                      type="text"
                      name="packagingMaterial"
                      value={editFormData.packagingMaterial}
                      onChange={handleEditChange}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      name="internalMeasurement"
                      value={editFormData.internalMeasurement}
                      onChange={handleEditChange}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      name="weightLimit"
                      value={editFormData.weightLimit}
                      onChange={handleEditChange}
                    />
                  </td>
                  <td>
                    <input
                      type="text"
                      name="description"
                      value={editFormData.description}
                      onChange={handleEditChange}
                    />
                  </td>
                  <td>{material.lastModified}</td>
                  <td>
                    <button onClick={handleSaveClick} className="save-btn">
                      Save
                    </button>
                  </td>
                </>
              ) : (
                <>
                  <td>{material._id}</td>
                  <td>{material.packagingMaterial}</td>
                  <td>{material.internalMeasurement}</td>
                  <td>{material.weightLimit}</td>
                  <td>{material.description}</td>
                  <td>{material.lastModified}</td>
                  <td>
                    <button
                      onClick={() => handleEditClick(index)}
                      className="edit-btn"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteClick(material._id)}
                      className="delete-btn"
                    >
                      Delete
                    </button>
                  </td>
                </>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Materials;
